function stopScrolling(){document.body.classList.toggle("no-scroll")}function openAccordion(o){o.parentNode.classList.toggle("accordion-open")}function openDropdown(o){o.parentNode.classList.toggle("dropdown-open")}function openModal(o){o.parentNode.classList.toggle("modal-open"),stopScrolling()}function openNav(o){o.parentNode.classList.toggle("nav-open"),stopScrolling()}